    @Test
    public void toJSON() throws JSONException {
        String nameJSON = "name";
        String latJSON = "lat";
        String lonJSON = "lon";
        String timezoneJSON = "timezone";
        String currentJSON = "current";
        String minutelyJSON = "minutely";
        String hourlyJSON = "hourly";
        String dailyJSON = "daily";

        JSONObject obj = city.toJSON();
        assertEquals("testCityName", obj.getString(nameJSON));
        assertEquals(50.07d, obj.getDouble(latJSON), 0d);
        assertEquals(22.23d, obj.getDouble(lonJSON), 0d);
        assertEquals("Europe/Warsaw", obj.getString(timezoneJSON));
        assertTrue(obj.has(currentJSON));
        assertTrue(obj.has(minutelyJSON));
        assertTrue(obj.has(hourlyJSON));
        assertTrue(obj.has(dailyJSON));
    }

