    @Test
    public void getLatAsString() throws JSONException {
        AppData.fromJSON(new JSONObject(APP_DATA));
        assertEquals("50", city.getLatAsString());
        AppData.fromJSON(new JSONObject(APP_DATA_1));
        assertEquals("50.0700", city.getLatAsString());
        AppData.fromJSON(new JSONObject(APP_DATA_2));
        assertEquals("50,07", city.getLatAsString());
    }

    @Test(expected = CoordinateOutOfBoundsException.class)
    public void setLat() throws CoordinateOutOfBoundsException {
        city.setLat(-180.1d);
    }

